mapper.select:

SELECT a.* , coalesce(R.ROLES,'USER') ROLES FROM (
	SELECT a.rco_gb companycode, a.asabn accountid, passwd password, a.kname name, a.mobi_no phone, coalesce(em.email,'') email  FROM TSILIB.EMP_MST A
	LEFT OUTER JOIN PCMLIB.ORG_USER em ON A.ASABN=em.empId
	WHERE a.asabn = #{accountId}
	UNION ALL
	SELECT 'SEOHAN' companycode, accountid, pswd password,  name, HTEL  phone,  '' email FROM SMLIB.VUSERPF A
	WHERE ACCOUNTID =  #{accountId}
) A
LEFT OUTER JOIN TSILIB.EMP_MST_ROLES R ON A.accountId=R.accountid
